import { AuthCardTitle as Title } from "@/features/auth-card-title/AuthCardTitle";
import { AuthCard } from "@/features/auth-card/AuthCard";
import useTranslation from "next-translate/useTranslation";
import { useState } from "react";
import { useRouter } from "next/router";

export const FormData = () => {
  let { t } = useTranslation("common");
  const [selectedOption, setSelectedOption] = useState(false);
  const router = useRouter();
  const isActive = "bg-primary text-white";
  const notActive = "bg-white";
  const defaultClass =
    "border border-border-color w-full p-3 flex items-center justify-center gap-3 rounded-[10px] mt-5 cursor-pointer";

  function handleOptionChange(option) {
    setSelectedOption(option);
    localStorage.setItem("purposeOfApplication", option);
    if (option == "master") router.push("/auth/check-user-master");
    else if (option == "transfer") router.push("/auth/check-user-transfer");
    else router.push("/auth/check-user");
  }

  return (
    <AuthCard className="w-[596px]  flex flex-col items-center bg-white rounded-[10px] px-5 py-7  mt-5  mx-auto">
      <Title title={t("auth.title")} />
      <div
        onClick={() => handleOptionChange("abituryent")}
        className={` ${
          selectedOption === "abituryent" ? isActive : notActive
        } ${defaultClass}`}
      >
        {Abituryent()}
        <p className="text-current text-lg font-semibold">Bakalavriat</p>
      </div>
      <div
        onClick={() => handleOptionChange("master")}
        className={` ${
          selectedOption === "master" ? isActive : notActive
        } ${defaultClass}`}
      >
        {Abituryent()}
        <p className="text-current text-lg font-semibold">Magistratura</p>
      </div>
      <div
        onClick={() => handleOptionChange("transfer")}
        className={` ${
          selectedOption === "transfer" ? isActive : notActive
        } ${defaultClass}`}
      >
        {Transfer()}
        <p className="text-current text-lg font-semibold">
          O’qishni ko’chirish
        </p>
      </div>
    </AuthCard>
  );
};

